import React, { useState, useEffect } from 'react';

const EmployeeForm = ({ employeeToUpdate, onUpdate, onSubmit }) => {
  const [formData, setFormData] = useState({
    name: '',
    employee_id: '',
    email: '',
    phone: '',
    department: '',
    date_of_joining: '',
    role: '',
  });

  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  // Use effect to populate the form when employeeToUpdate changes (for updating)
  useEffect(() => {
    if (employeeToUpdate) {
      setFormData({
        name: employeeToUpdate.name || '',
        employee_id: employeeToUpdate.employee_id || '',
        email: employeeToUpdate.email || '',
        phone: employeeToUpdate.phone || '',
        department: employeeToUpdate.department || '',
        date_of_joining: employeeToUpdate.date_of_joining || '',
        role: employeeToUpdate.role || '',
      });
    }
  }, [employeeToUpdate]); // Re-run when employeeToUpdate changes

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');

    try {
      if (employeeToUpdate) {
        // Update the employee if we have an employeeToUpdate
        const response = await fetch(`/api/employees/${employeeToUpdate.id}`, {
          method: 'PUT',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify(formData),
        });

        if (response.ok) {
          const data = await response.json();
          setSuccess(data.message || 'Employee updated successfully!');
          onUpdate(); // Callback to parent to refresh data
        } else {
          const errorData = await response.json();
          setError(errorData.message || 'Update failed.');
        }
      } else {
        // Create a new employee if no employeeToUpdate
        const response = await fetch('/api/employees', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify(formData),
        });

        if (response.ok) {
          const data = await response.json();
          setSuccess(data.message || 'Employee added successfully!');
          onSubmit(); // Callback to parent to refresh data
        } else {
          const errorData = await response.json();
          setError(errorData.message || 'Submission failed.');
        }
      }
      // Reset the form after successful submission
      setFormData({
        name: '',
        employee_id: '',
        email: '',
        phone: '',
        department: '',
        date_of_joining: '',
        role: '',
      });
    } catch (err) {
      setError('Error connecting to the server. Please try again.');
      console.error('Error:', err);
    }
  };

  return (
    <div>
      <h1>{employeeToUpdate ? 'Update Employee' : 'Add Employee'}</h1>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      {success && <p style={{ color: 'green' }}>{success}</p>}
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="name"
          placeholder="Name"
          value={formData.name}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="employee_id"
          placeholder="Employee ID"
          value={formData.employee_id}
          onChange={handleChange}
          required
          maxLength={10}
        />
        <input
          type="email"
          name="email"
          placeholder="Email"
          value={formData.email}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="phone"
          placeholder="Phone Number"
          value={formData.phone}
          onChange={handleChange}
          required
          pattern="\d{10}"
        />
        <select
          name="department"
          value={formData.department}
          onChange={handleChange}
          required
        >
          <option value="">Select Department</option>
          <option value="HR">HR</option>
          <option value="Engineering">Engineering</option>
          <option value="Marketing">Marketing</option>
        </select>
        <input
          type="date"
          name="date_of_joining"
          max={new Date().toISOString().split('T')[0]}
          value={formData.date_of_joining}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="role"
          placeholder="Role"
          value={formData.role}
          onChange={handleChange}
          required
        />
        <button type="submit">{employeeToUpdate ? 'Update' : 'Submit'}</button>
        <button
          type="reset"
          onClick={() =>
            setFormData({
              name: '',
              employee_id: '',
              email: '',
              phone: '',
              department: '',
              date_of_joining: '',
              role: '',
            })
          }
        >
          Reset
        </button>
      </form>
    </div>
  );
};

export default EmployeeForm;
