// In your React component (e.g., EmployeeDashboard.js)
import React, { useState } from 'react';
import EmployeeList from './EmployeeList';

const EmployeeDashboard = () => {
    const [employees, setEmployees] = useState([]);
    const [error, setError] = useState('');

    // Function to fetch employees data
    const viewDatabase = async () => {
        try {
            const response = await fetch('http://localhost:5000/api/employees');
            if (!response.ok) {
                throw new Error('Failed to fetch employees');
            }
            const data = await response.json();
            setEmployees(data);  // Set the fetched data to the state
        } catch (err) {
            setError('Error connecting to the server. Please try again.');
        }
    };

    return (
        <div>
            <h1>Employee Management Dashboard</h1>

            {/* View Database Button */}
            <button onClick={viewDatabase}>View Database</button>

            {/* Error Message */}
            {error && <p style={{ color: 'red' }}>{error}</p>}

            {/* Displaying Employees Data */}
            <table>
                <thead>
                    <tr>
                        <th>Name</th>
                        <th>Employee ID</th>
                        <th>Email</th>
                        <th>Phone</th>
                        <th>Department</th>
                        <th>Date of Joining</th>
                        <th>Role</th>
                    </tr>
                </thead>
                <tbody>
                    {employees.map((employee) => (
                        <tr key={employee.id}>
                            <td>{employee.name}</td>
                            <td>{employee.employee_id}</td>
                            <td>{employee.email}</td>
                            <td>{employee.phone}</td>
                            <td>{employee.department}</td>
                            <td>{employee.date_of_joining}</td>
                            <td>{employee.role}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default EmployeeDashboard;
