import React, { useState, useEffect } from 'react';


const Dashboard = () => {
  const [employees, setEmployees] = useState([]);
  const [selectedEmployee, setSelectedEmployee] = useState(null);
  const [error, setError] = useState('');
  const [isDatabaseVisible, setIsDatabaseVisible] = useState(false);

  // Fetch employee data when the component mounts
  useEffect(() => {
    viewDatabase();
  }, []); // Empty dependency array ensures this runs only once when the component mounts

  const viewDatabase = async () => {
    try {
      const response = await fetch('/api/employees');
      if (!response.ok) {
        throw new Error('Failed to fetch employees');
      }
      const data = await response.json();
      setEmployees(data);
      setIsDatabaseVisible(true);
    } catch (err) {
      setError('Error connecting to the server. Please try again.');
    }
  };

  const handleUpdateClick = (employee) => {
    setSelectedEmployee(employee);
  };

  const refreshDatabase = () => {
    viewDatabase();
    setSelectedEmployee(null);
  };

  const deleteEmployee = async (id) => {
    try {
      const response = await fetch(`/api/employees/${id}`, {
        method: 'DELETE',
      });

      if (!response.ok) {
        throw new Error('Failed to delete employee.');
      }

      setEmployees((prevEmployees) => prevEmployees.filter((employee) => employee.id !== id));
    } catch (err) {
      setError('Error deleting employee. Please try again.');
    }
  };

  return (
    <div>
      <h1>Employee Management Dashboard</h1>
      <EmployeeForm onSubmit={refreshDatabase} employeeToUpdate={selectedEmployee} />
      <button onClick={viewDatabase}>View Database</button>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      {isDatabaseVisible && employees.length > 0 ? (
        <table>
          <thead>
            <tr>
              <th>Name</th>
              <th>Employee ID</th>
              <th>Email</th>
              <th>Phone</th>
              <th>Department</th>
              <th>Date of Joining</th>
              <th>Role</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {employees.map((employee) => (
              <tr key={employee.id}>
                <td>{employee.name}</td>
                <td>{employee.employee_id}</td>
                <td>{employee.email}</td>
                <td>{employee.phone}</td>
                <td>{employee.department}</td>
                <td>{employee.date_of_joining}</td>
                <td>{employee.role}</td>
                <td>
                  <button onClick={() => handleUpdateClick(employee)}>Update</button>
                  <button onClick={() => deleteEmployee(employee.id)}>Delete</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        isDatabaseVisible && <p>No employees found.</p>
      )}
    </div>
  );
};

export default Dashboard;
